import './App.css';
import Spinner from './Spinner';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faXmark } from "@fortawesome/free-solid-svg-icons";
import { Route, Routes, Navigate, useNavigate, useLocation } from "react-router-dom";
import Video from './Video';
import Login from './Login';
import { useState, useEffect } from 'react';
import Menubar from './Menubar';
import axios from 'axios';
import Book from './Book';
import Pdf from './Pdf';


function App() {
  const [login, setLogin] = useState(false);
  const [menu, setMenu] = useState(false);
  const [auth, setAuth] = useState(false); // Assuming this controls some auth logic
  const [error, setError] = useState('');
  const [page, setPage] = useState('books')
  const [loading, setLoading] = useState(true)
  const location = useLocation()

  const navigate = useNavigate();

  useEffect(()=>{
    const checkLogin = async()=>{
      try{
      const resp = await axios.get('http://localhost:5000/verifyToken', {withCredentials: true})
      setAuth(true)
      console.log(resp)
      setLoading(false)
    }catch(e){
      console.log(e)
      setLoading(false)
      navigate('/login', {replace:true})
    }}
    checkLogin()
  }, [])

  // Toggle menu visibility
  const toggleMenu = () => setMenu(!menu);

  return (
    <div className="app-container">
      {/* Optional Spinner */}
      {loading ?<Spinner bg={"white"} />:
        <>
      {(auth && !/\/books\/.{5,}/.test(location.pathname)) && (<>
        <header className="app-header">
          <p>IIM Nagpur - SMP05 Batch</p>
          <p style={{ fontSize: "26px" }} onClick={toggleMenu}>
            <FontAwesomeIcon
              icon={faBars}
              className={`menu-icon ${menu ? "hide" : ""}`}
            />
            <FontAwesomeIcon
              icon={faXmark}
              className={`menu-icon ${!menu ? "hide" : ""}`}
            />
          </p>
        </header>
        <Menubar setMenu = {setMenu} setError = {setError} width= {menu ? "150px" : "0px"} page={page} setPage={setPage} setAuth = {setAuth}  /></>
      )}

      <Routes>
        <Route path="/login" element={<Login setAuth={setAuth} />} />
        <Route path="/videos" element={<ProtectedRoute auth={auth} Children={<Video />}></ProtectedRoute>} />
        <Route path ="/books" element={<ProtectedRoute auth={auth} Children={<Book setError={setError} />} />} />
        <Route  path="/books/:id" element ={<ProtectedRoute auth={auth} Children={<Pdf  />} />} />
        <Route path="*" element={<Navigate to="/videos" />} />

      </Routes>

      </>}
    </div>
  );
}

function ProtectedRoute({Children, auth}){
  const navigate = useNavigate()
  useEffect(()=>{
    console.log('protected')
    if(!auth){
      console.log('protected')
      navigate('/login')
    }
  })
  return Children 

}

export default App;
