import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Worker, Viewer } from "@react-pdf-viewer/core";
import "@react-pdf-viewer/core/lib/styles/index.css";

const Pdf = () => {
  const { id } = useParams();
  const [pdfUrl, setPdfUrl] = useState(null);

  useEffect(() => {
    const fetchPdf = async () => {
      try {
        const response = await fetch(`http://localhost:5000/download/${id}`);
        if (!response.ok) throw new Error("Failed to fetch PDF");
        const blob = await response.blob();
        setPdfUrl(URL.createObjectURL(blob));
      } catch (error) {
        console.error("Error loading PDF:", error);
      }
    };
    if (id) fetchPdf();
  }, [id]);

  return (
    <div style={{ height: "90vh", width: "100%", position: "relative", overflow: "hidden" }}>
      <button
        onClick={() => navigate(-1)}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          padding: "8px 12px",
          backgroundColor: "#f44336",
          color: "white",
          border: "none",
          borderRadius: "4px",
          cursor: "pointer",
        }}
      >
        Close
      </button>
      <h2 style={{ textAlign: "center" }}>PDF Viewer</h2>
      {pdfUrl ? (
        <Worker workerUrl={`https://unpkg.com/pdfjs-dist@3.11.174/build/pdf.worker.min.js`}>
          <Viewer fileUrl={pdfUrl} />
        </Worker>
      ) : (
        <p style={{ textAlign: "center" }}>Loading PDF...</p>
      )}
    </div>
  );
};

export default Pdf;
