  import './App.css';

function App() {
  return (
    <>
     <>
     {loading && <Spinner bg={"white"} />}
      {!login && (
        <>
          <header>
            <p>IIM Nagpur - SMP05 Batch</p>

            <p style={{ fontSize: "26px" }} onClick={transistion}>
              <FontAwesomeIcon
                icon={faBars}
                className={`menu-icon ${menu ? "hide" : ""}`}
                id="menu"
              />
              <FontAwesomeIcon
                icon={faXmark}
                className={`menu-icon ${!menu ? "hide" : ""}`}
                id="close"
              />
            </p>
          </header>
          <Menubar
            width={menu ? "150px" : "0px"}
            setMenu={(x) => dispatch(setMenu())}
            page={page}
            setError={setError}
            setPage={() => dispatch(setPage())}
            auth={auth}
          />
        </>
      )}
      <Routes>
        <Route
          path="/login"
          element={auth ? <Navigate path="/books" /> : <Login />}
        />
        <Route
          path="/videos"
          element={auth ? <Videos /> : <Navigate path="/login" />}
        />
        <Route path ="/books" element ={<Home />} />
        <Route path="/edit" element={<Editpage />} />
        <Route path="*" element={<Navigate path="/books" />} />
        <Route path={"/books/:id"} element={close && <Book ebooks={ebooks} close = {setClose} />} />

      </Routes>
    </>
    </>
  );
}

export default App;
