import {useState, useEffect} from 'react';



export default function ({data, name}){
    const [videos, setVideos] = useState({});
    const [images, setImages] = useState({});
  
    useEffect(() => {
      const imageMap = {};
      const videoMap = {};
  
      data.forEach((e) => {
        const key = e.name.split(".")[0]; // Remove extension
        if (/image/.test(e.mimeType)) {
          imageMap[key] = e.id; // Store in temp object
        } else {
          videoMap[key] = e.id;
        }
      });
  
      setImages(imageMap);
      setVideos(videoMap);
    }, [data]);

    return(<>
        <div>
      <p className="sec-title">{name}</p>
      <div className="video-box">
        {data?.length == 0 && (
          <div className="empty-box">
            No videos available at this time
          </div>
        )}

        {/* {highlight && (
          <Highlight
            data={detail}
            close={setHighlight}
            change={changed}
            user={user}
            sectionObj={sectionObj.find((e) => e.name == detail.sectionName)}
            setMessage = {setMessage}
          /> //highlight
        )} */}

        {/* {Object.keys(vide)?.map((data, i, arr) => {
        
          return (
            <>
              <div
                className="vid-box"
                key={i}
                onClick={() => {
                  setDetail(vide[data]);
                  type == "book"
                    ? navigator("/books/" + data?.id)
                    : setHighlight(true);
                }}
              >
                <img
                  src={ thumb[data] ? `http:localhost:5000/download/${thumb[data]}`: download }
                  height="200px"
                  alt="no Image"
                /> 
                <p>{data}</p>     

              </div>
            </>
          );
        })} */}

      </div>
    </div>
    </>)
}
