import {useState, useEffect} from 'react';



export default function ({data, name}){

const [videos, setVideos] = useState({})
const [images, setImages] = useState({})

data.forEach((e)=>{
    if(/image/.test(e.mimeType)){
        setImages(prev=>({...prev, [e.name.split('.')[0]]: e.id}))
    }else{
        setVideos(prev=>({...prev, [e.name.split('.')[0]]: e.id}))
    }

})


    return(<>
        <div>
      <p className="sec-title">{name}</p>
      <div className="video-box">
        {data?.length == 0 && (
          <div className="empty-box">
            No videos available at this time
          </div>
        )}

        {/* {highlight && (
          <Highlight
            data={detail}
            close={setHighlight}
            change={changed}
            user={user}
            sectionObj={sectionObj.find((e) => e.name == detail.sectionName)}
            setMessage = {setMessage}
          /> //highlight
        )} */}

        {Object.keys(vide)?.map((data, i, arr) => {
        
          return (
            <>
              <div
                className="vid-box"
                key={i}
                onClick={() => {
                  setDetail(vide[data]);
                  type == "book"
                    ? navigator("/books/" + data?.id)
                    : setHighlight(true);
                }}
              >
                <img
                  src={ thumb[data] ? `http:localhost:5000/download/${thumb[data]}`: download }
                  height="200px"
                  alt="no Image"
                /> 
                <p>{data}</p>     

              </div>
            </>
          );
        })}

      </div>
    </div>
    </>)
}
