import React from "react";
import { useParams } from "react-router-dom";
import { Worker, Viewer } from "@react-pdf-viewer/core";
import "@react-pdf-viewer/core/lib/styles/index.css";
import {useState, useEffect} from 'react';

const Pdf = () => {
  const { id } = useParams();
  const [pdf, setPdf] = useState('')
useEffect(()=>{
    const fetchfile = async()=>{
        const resp = await fetch(`http://localhost:5000/download/${id}`)
        const file = await resp.arrayBuffer();
        console.log(file)
        if(file){
            // const url = URL.createObjectURL(file)
            setPdf(file)
        }
    }
    fetchfile()
}, [])

  

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <h2 style={{ textAlign: "center" }}>PDF Viewer</h2>
      ((id && pdf)? (
        <Worker workerUrl={`https://unpkg.com/pdfjs-dist@3.11.174/build/pdf.worker.min.js`}>
          <Viewer fileUrl={pdf} />
        </Worker>
      ) : (
        <p style={{ textAlign: "center" }}>Something went wrong. Please try after some time.</p>
      )}
    </div>
  );
};

export default Pdf;
