import './App.css';
import Spinner from './Spinner';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faXmark } from "@fortawesome/free-solid-svg-icons";
import { Route, Routes, Navigate } from "react-router-dom";
import Video from './Video';
import Login from './Login';
import { useState } from 'react';

function App() {
  const [login, setLogin] = useState(false);
  const [menu, setMenu] = useState(false);
  const auth = true; // Assuming this controls some auth logic

  // Toggle menu visibility
  const toggleMenu = () => setMenu(!menu);

  return (
    <div className="app-container">
      {/* Optional Spinner */}
      {/* {loading && <Spinner bg={"white"} />} */}

      {!login && (
        <header className="app-header">
          <p>IIM Nagpur - SMP05 Batch</p>
          <p style={{ fontSize: "26px" }} onClick={toggleMenu}>
            <FontAwesomeIcon
              icon={faBars}
              className={`menu-icon ${menu ? "hide" : ""}`}
            />
            <FontAwesomeIcon
              icon={faXmark}
              className={`menu-icon ${!menu ? "hide" : ""}`}
            />
          </p>
        </header>
      )}

      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/videos" element={<Video />} />
        {/* Redirect any unknown routes to /videos */}
        <Route path="*" element={<Navigate to="/videos" />} />
      </Routes>
    </div>
  );
}

export default App;
